import React, { useState } from "react";
import styled from "@emotion/styled";
import ErrorBoundary from "react-error-boundary";
import {
  Card,
  CardBody,
  Button,
  Form,
  FormGroup,
  Label,
  Input,
  FormText,
} from "reactstrap";
import Banner from "./Banner";
import PropTypes from 'prop-types';


const LoginLayout = styled.div`
display: grid;
grid-template-areas:
  "banner banner banner"
  "search list detail";
grid-template-rows: 140px 1fr;
grid-template-columns: 5fr;
width: 100vw;
min-height: 100vh;
background: #5e9668;
`;

const LoginCard = styled(Card)`
  background-color: #383a3f !important;
  background-color: #3e4348 !important;
  background-color: #1f2124 !important;
  background-color: #011627 !important;
`;

const LoginContent = styled.div`
  grid-area: content;
  position: absolute;
  top: 150px;

`;

export default function LoginAnon(props) {
  return (
    <ErrorBoundary>
      <LoginLayout>
        <Banner />
        <LoginContent>
          <LoginForm {...props} />
        </LoginContent>
      </LoginLayout>
    </ErrorBoundary>
  );
}

const ButtonRow = styled.div`
  display: flex;
  flex-direction: row;
  justify-content: space-between;
`;

export function LoginForm(props) {
  const { loginAnonymous } = props;
  const handleLogin = () => {
    loginAnonymous();
  };

  return (
    <LoginCard inverse color="dark">
      <CardBody>
        <Form>
          <ButtonRow>
            <Button onClick={handleLogin}>Log in as Anonymous</Button>
          </ButtonRow>
        </Form>
      </CardBody>
    </LoginCard>
  );
}

LoginForm.propTypes = {
   loginAnonymous: PropTypes.any,
 };
