import { Stitch, RemoteMongoClient } from "mongodb-stitch-browser-sdk";

const APP_ID = "todo_tutorial-qcdpf";

// Stitch App Client Setup
if (!Stitch.hasAppClient(APP_ID)) {
  Stitch.initializeAppClient(APP_ID);
}
const app = Stitch.getAppClient(APP_ID);

const db = app.getServiceClient(RemoteMongoClient.factory, "mongodb-atlas").db('todo');


  let items = db.collection("items");
  let users = db.collection("users");

export {app, items, users};
